defmodule SantasFloor do
  def verify_floor(input) do
    String.graphemes(input)
    |> verify_floor(0)
  end

  defp verify_floor([head | tail], floor) do
    case head do
      "(" -> verify_floor(tail, floor + 1)
      ")" -> verify_floor(tail, floor - 1)
      _ -> 0
    end
  end

  defp verify_floor([], floor) do
    floor
  end

  def find_first_basement_enter(input) do
    String.graphemes(input)
    |> find_first_basement_enter(0, 0)
  end

  defp find_first_basement_enter([head | tail], floor, position) when floor >= 0 do
    floor = verify_floor([head], floor)
    find_first_basement_enter(tail, floor, position + 1)
  end

  defp find_first_basement_enter(_, -1, position) do
    position
  end

  defp find_first_basement_enter([], _, _) do
    nil
  end
end

floor = SantasFloor.verify_floor(input)
basement = SantasFloor.find_first_basement_enter(input)

IO.puts("floor: #{floor}")
IO.puts("basement: #{basement}")
