defmodule SantasFloor do
  def verify_floor(floor, []), do: floor
  def verify_floor(floor, "("), do: floor + 1
  def verify_floor(floor, ")"), do: floor - 1

  def verify_floor(floor, [head | tail]) do
    floor
    |> verify_floor(head)
    |> verify_floor(tail)
  end

  def find_first_basement_enter(-1, _, position),do: position
  def find_first_basement_enter(_, [], _), do: nil

  def find_first_basement_enter(floor, [head | tail], position) do
    floor
    |> verify_floor(head)
    |> find_first_basement_enter(tail, position + 1)
  end
end

directions = String.graphemes(input)

floor = SantasFloor.verify_floor(0, directions)
basement = SantasFloor.find_first_basement_enter(0, directions, 0)

IO.puts("floor: #{floor}")
IO.puts("basement: #{basement}")
